INSERT INTO `tintuc` (`id`, `danhmuc_id`, `active`, `name`, `name_url`, `img`, `show_index`, `tin_moi`, `tin_noi_bat`, `link_video`, `file_mp4`, `noi_dung`, `title`, `keyword`, `description`, `created`) VALUES
(1, 1, 1, 'Thị ủy Từ Sơn tổ chức học tập, quán triệt Nghị quyết Đại hội lần thứ XII của Đảng', 'thi-uy-tu-son-to-chuc-hoc-tap-quan-triet-nghi-quyet-dai-hoi-lan-thu-xii-cua-dang', '/daituson/view/admin/Themes/kcfinder/upload/images/tintuc/TS160509_3.jpg', 0, 1, 1, '', '/daituson/view/admin/Themes/kcfinder/upload/images/file_mp4/thoi_su_tong_hop/thi_uy_tu_son/TS160712_1.mp4', '<p>\r\n	<span style="color: rgb(60, 59, 59); font-family: helvetica; font-size: 15px; text-align: justify;">Hưởng ứng phong tr&agrave;o x&acirc;y dựng n&ocirc;ng th&ocirc;n mới, những năm qua thị uỷ, UBND thị x&atilde; Từ Sơn đ&atilde; t&iacute;ch cực chỉ đạo c&aacute;c ng&agrave;nh đo&agrave;n thể v&agrave; c&aacute;c x&atilde;, phường tr&ecirc;n địa b&agrave;n tập trung mọi nguồn lực thực hiện c&aacute;c chỉ ti&ecirc;u về x&acirc;y dựng n&ocirc;ng th&ocirc;n mới. &nbsp;T&iacute;nh đến hết năm 2015, to&agrave;n thị x&atilde; c&oacute; 4/5 x&atilde; đ&atilde; ho&agrave;n th&agrave;nh 19/19 ti&ecirc;u ch&iacute; được c&ocirc;ng nhận đạt ti&ecirc;u chuẩn x&acirc;y dựng n&ocirc;ng th&ocirc;n mới, &nbsp;c&ograve;n lại x&atilde; Tam Sơn l&agrave; đơn vị cuối c&ugrave;ng của thị x&atilde; chưa ho&agrave;n th&agrave;nh chỉ ti&ecirc;u. Với mục ti&ecirc;u 100% x&atilde; tr&ecirc;n địa b&agrave;n đạt ti&ecirc;u chuẩn về x&acirc;y dựng n&ocirc;ng th&ocirc;n mới để tạo tiền đề cho Từ Sơn sớm trở th&agrave;nh đ&ocirc; thị loại 3 văn minh hiện đại v&agrave;o năm 2020, Thị x&atilde; đ&atilde; chỉ đạo x&atilde; Tam Sơn phấn đấu trong năm 2016 sẽ ho&agrave;n th&agrave;nh 19/19 chỉ ti&ecirc;u về x&acirc;y dựng n&ocirc;ng th&ocirc;n mới.</span></p>\r\n', 'Thị ủy Từ Sơn tổ chức học tập, quán triệt Nghị quyết Đại hội lần thứ X', 'Thị ủy Từ Sơn tổ chức học tập, quán triệt Nghị quyết Đại hội lần thứ XII của Đảng', 'Thị ủy Từ Sơn tổ chức học tập, quán triệt Nghị quyết Đại hội lần thứ XII của Đảng', '2017-05-07 05:05:47'),
